package com.github.ferinagy.adventOfCode.aoc2015

fun main() {
    println("Part1:")
    println(calculateFloor())

    println()
    println("Part2:")
    println(calculateBasement())
}

private fun calculateFloor(): Int {
    val map = mapOf('(' to 1, ')' to -1)
    return input.sumOf { map[it]!! }
}

private fun calculateBasement(): Int {
    var positon = 0
    var floor = 0
    val map = mapOf('(' to 1, ')' to -1)
    while (0 <= floor) {
        floor += map[input[positon]]!!
        positon++
    }

    return positon
}

